package com.hamxidesigner.wine.app.Fragmentimport android.content.Intentimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.view.animation.AnimationUtilsimport android.widget.ImageViewimport androidx.core.content.ContextCompatimport androidx.fragment.app.Fragmentimport com.hamxidesigner.wine.app.Activities.MainActivityimport com.hamxidesigner.wine.app.Activities.SearchingActivityimport com.hamxidesigner.wine.app.Rclass FragmentMainDashBoard : Fragment() {    private lateinit var mView: View    lateinit var txt_feed: ImageView    lateinit var txt_search: ImageView    lateinit var txt_friends: ImageView    lateinit var txt_favr: ImageView    lateinit var txt_wish: ImageView    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {        mView = inflater.inflate(R.layout.content_main_fragment, container, false)        if (savedInstanceState == null) {            val detailsFragment = FragmentFeed().newInstance()            childFragmentManager                    .beginTransaction()                    .replace(R.id.content_framee, detailsFragment, null)                    //.addToBackStack(null)                    .commit()        }        initViews()        initObect()        clickView()        return mView    }    private fun initViews() {        txt_feed = mView.findViewById(R.id.txt_feed)        txt_search = mView.findViewById(R.id.txt_search)        txt_friends = mView.findViewById(R.id.txt_friends)        txt_favr = mView.findViewById(R.id.txt_favr)        txt_wish = mView.findViewById(R.id.txt_wish)    }    private fun clickView() {        txt_feed.setOnClickListener {            (activity as MainActivity).getSupportActionBar()?.setTitle("Feed")            val aniFade = AnimationUtils.loadAnimation(context, R.anim.model_in)            txt_feed.startAnimation(aniFade)            txt_feed.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.circle_feed))            txt_search.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_search))            txt_friends.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_about))            txt_favr.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_favorite))            txt_wish.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_local_drink_white))            val detailsFragment = FragmentFeed().newInstance()            childFragmentManager                    .beginTransaction()                    .replace(R.id.content_framee, detailsFragment, null)            //.addToBackStack(null)                    .commit()        }        txt_search.setOnClickListener {            val aniFade = AnimationUtils.loadAnimation(context, R.anim.model_in)            txt_search.startAnimation(aniFade)            txt_feed.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.circle_feed))            txt_search.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_search))            txt_friends.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_about))            txt_favr.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_favorite))            txt_wish.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_local_drink_white))            var intent = Intent(context, SearchingActivity::class.java)            intent.putExtra("data","")            startActivity(intent)            activity!!.overridePendingTransition(R.anim.slide_in, R.anim.slide_out)        }        txt_friends.setOnClickListener {            (activity as MainActivity?)!!.supportActionBar!!.title = "Friends"            val aniFade = AnimationUtils.loadAnimation(context, R.anim.model_in)            txt_friends.startAnimation(aniFade)            txt_feed.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_autorenew))            txt_search.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_search))            txt_friends.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.circle_friends))            txt_favr.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_favorite))            txt_wish.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_local_drink_white))            val detailsFragment = FragmentFriends().newInstance()            childFragmentManager                    .beginTransaction()                    .replace(R.id.content_framee, detailsFragment, null)                    //.addToBackStack(null)                    .commit()        }        txt_favr.setOnClickListener {            (activity as MainActivity?)!!.supportActionBar!!.title = "Favourite"            val aniFade = AnimationUtils.loadAnimation(context, R.anim.model_in)            txt_favr.startAnimation(aniFade)            txt_feed.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_autorenew))            txt_search.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_search))            txt_friends.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_about))            txt_favr.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.circle_fav))            txt_wish.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_local_drink_white))            val detailsFragment = FragmentFav().newInstance()            childFragmentManager                    .beginTransaction()                    .replace(R.id.content_framee, detailsFragment, null)                    //.addToBackStack(null)                    .commit()        }        txt_wish.setOnClickListener {            (activity as MainActivity?)!!.supportActionBar!!.title = "Wishlist"            val aniFade = AnimationUtils.loadAnimation(context, R.anim.model_in)            txt_wish.startAnimation(aniFade)            txt_feed.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_autorenew))            txt_search.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_search))            txt_friends.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_about))            txt_favr.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.ic_favorite))            txt_wish.setImageDrawable(ContextCompat.getDrawable(activity!!, R.drawable.circle_wish))            val detailsFragment = FragmentWishlist().newInstance()            childFragmentManager                    .beginTransaction()                    .replace(R.id.content_framee, detailsFragment, null)                    //.addToBackStack(null)                    .commit()        }    }    private fun initObect() {    }    fun newInstance(): FragmentMainDashBoard {        return FragmentMainDashBoard()    }}