package com.hamxidesigner.wine.appimport android.content.Intentimport android.os.Bundleimport android.os.Handlerimport android.widget.ImageViewimport android.widget.TextViewimport androidx.appcompat.app.AppCompatActivityimport com.hamxidesigner.wine.app.Activities.LoginActivityimport com.hamxidesigner.wine.app.Activities.MainActivityimport com.hamxidesigner.wine.app.Helper.SessionManagerclass SplahActivity : AppCompatActivity() {    lateinit var mHandler: Handler    lateinit var sessionManager: SessionManager    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_splah)        initViews()        initObject()        clickViews()    }    private fun clickViews() {    }    private fun initObject() {        sessionManager = SessionManager(applicationContext)        mHandler = Handler()        mHandler.postDelayed({            if(sessionManager.keyId.equals("false")){                val intent = Intent(this, LoginActivity::class.java)                startActivity(intent)                overridePendingTransition(R.anim.slide_in, R.anim.slide_out)                finish()            }            if(sessionManager.keyId.equals("true")){                val intent = Intent(this, MainActivity::class.java)                startActivity(intent)                overridePendingTransition(R.anim.slide_in, R.anim.slide_out)                finish()            }        }, 4500)    }    private fun initViews() {    }}